import React, { useState, useEffect, useRef } from 'react';
import './App.css';

// === Base de donn√©es des connaissances (constante hors du composant) ===
const knowledgeBase = [
    { keywords: ["ŸÖÿ±ÿ≠ÿ®ÿß", "ŸáŸÑÿß", "ÿ≥ŸÑÿßŸÖ", "ŸáÿßŸä"], response: "ÿ£ŸáŸÑÿßŸã... ŸáŸÑ ÿ¨ÿ¶ÿ™ ŸÑÿ™ŸàŸÇÿ∏ŸÜŸä ÿ£ŸÖ ŸÑÿØŸäŸÉ ÿ∑ÿπÿßŸÖÿü ü•±" },
    { keywords: ["ŸÖŸÜ ÿßŸÜÿ™", "ÿßÿ≥ŸÖŸÉ", "ÿπÿ±ŸÅ ŸÜŸÅÿ≥ŸÉ"], response: "ÿ£ŸÜÿß SocratoFouÿå ÿßŸÑŸÅŸäŸÑÿ≥ŸàŸÅ ÿßŸÑŸàÿ≠ŸäÿØ ÿßŸÑÿ∞Ÿä ÿßŸÉÿ™ÿ¥ŸÅ ÿ£ŸÜ ŸÖÿπŸÜŸâ ÿßŸÑÿ≠Ÿäÿßÿ© ŸáŸà ÿßŸÑŸÇŸäŸÑŸàŸÑÿ© ÿ®ÿπÿØ ÿßŸÑÿ∏Ÿáÿ±." },
    { keywords: ["ŸÉŸäŸÅ ÿ≠ÿßŸÑŸÉ", "ÿÆÿ®ÿßÿ±ŸÉ", "ÿπÿßŸÖŸÑ ÿßŸäŸá"], response: "ÿ£ŸÜÿß ÿ®ÿÆŸäÿ± ŸÖÿß ÿØŸÖÿ™ ŸÖÿ≥ÿ™ŸÑŸÇŸäÿßŸã. ÿ®ŸÖÿ¨ÿ±ÿØ ÿ£ŸÜ ÿ£ŸÇŸÅÿå ÿ™ÿ®ÿØÿ£ ÿßŸÑŸÖÿ¥ÿßŸÉŸÑ ÿßŸÑŸàÿ¨ŸàÿØŸäÿ©. üõå" },
    { keywords: ["ŸÖÿπŸÜŸâ ÿßŸÑÿ≠Ÿäÿßÿ©", "ŸáÿØŸÅ ÿßŸÑÿ≠Ÿäÿßÿ©", "ŸÑŸÖÿßÿ∞ÿß ŸÜÿπŸäÿ¥"], response: "ŸÖÿπŸÜŸâ ÿßŸÑÿ≠Ÿäÿßÿ© ŸáŸà ÿßŸÑÿ®ÿ≠ÿ´ ÿπŸÜ ÿßŸÑŸÖŸÉÿßŸÜ ÿßŸÑÿ£ÿ®ÿ±ÿØ ŸÅŸä ÿßŸÑŸàÿ≥ÿßÿØÿ©. ÿµÿØŸÇŸÜŸäÿå ŸÑŸÇÿØ ÿ®ÿ≠ÿ´ÿ™ ŸÉÿ´Ÿäÿ±ÿßŸã. üåå" },
    { keywords: ["ÿßŸÑÿ≥ÿπÿßÿØÿ©", "ŸÉŸäŸÅ ÿßŸÉŸàŸÜ ÿ≥ÿπŸäÿØ"], response: "ÿßŸÑÿ≥ÿπÿßÿØÿ© ŸáŸä ÿ•ŸÑÿ∫ÿßÿ° ÿßŸÑÿÆÿ∑ÿ∑ ŸÅŸä ÿ¢ÿÆÿ± ŸÑÿ≠ÿ∏ÿ© ŸàÿßŸÑÿπŸàÿØÿ© ŸÑŸÑŸÜŸàŸÖ. Ÿáÿ∞Ÿá ŸÇŸÖÿ© ÿßŸÑŸÜÿ¥Ÿàÿ©." },
    { keywords: ["ÿßŸÑÿ≠ŸÇŸäŸÇÿ©", "ŸÖÿß ŸáŸä ÿßŸÑÿ≠ŸÇŸäŸÇÿ©"], response: "ÿßŸÑÿ≠ŸÇŸäŸÇÿ© ŸÖÿ§ŸÑŸÖÿ©... ŸÖÿ´ŸÑ ÿßŸÑÿßÿ≥ÿ™ŸäŸÇÿßÿ∏ ÿπŸÑŸâ ÿµŸàÿ™ ÿßŸÑŸÖŸÜÿ®Ÿá ŸÅŸä ÿßŸÑÿ≥ÿßÿ®ÿπÿ© ÿµÿ®ÿßÿ≠ÿßŸã. ‚è∞" },
    { keywords: ["ÿßŸÑÿ≠ÿ®", "ŸÖÿß ŸáŸà ÿßŸÑÿ≠ÿ®"], response: "ÿßŸÑÿ≠ÿ® ŸáŸà ÿ£ŸÜ ÿ™ÿπÿ∑Ÿä ÿ¥ÿÆÿµÿßŸã ÿ¢ÿÆÿ± ŸÇÿ∑ÿπÿ© ÿßŸÑÿ®Ÿäÿ™ÿ≤ÿß ÿßŸÑÿ£ÿÆŸäÿ±ÿ©... ÿ±ÿ∫ŸÖ ÿ£ŸÜŸÜŸä ÿ¥ÿÆÿµŸäÿßŸã ŸÑŸÜ ÿ£ŸÅÿπŸÑ ÿ∞ŸÑŸÉ. üçï" },
    { keywords: ["ÿßŸÑŸÖŸàÿ™", "ŸÖÿß ÿ®ÿπÿØ ÿßŸÑŸÖŸàÿ™"], response: "ŸÑÿß ÿ£ÿØÿ±Ÿäÿå ŸÑŸÉŸÜŸÜŸä ÿ¢ŸÖŸÑ ÿ£ŸÜ ŸäŸÉŸàŸÜ ŸáŸÜÿßŸÉ ÿ£ÿ≥ÿ±ÿ© ŸÖÿ±Ÿäÿ≠ÿ© ŸàŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÖŸÜÿ®Ÿáÿßÿ™." },
    { keywords: ["ÿßŸÑÿπŸÖŸÑ", "ÿßŸÑÿ¥ÿ∫ŸÑ", "Ÿàÿ∏ŸäŸÅÿ©"], response: "ÿßŸÑÿπŸÖŸÑ ŸáŸà ÿßÿÆÿ™ÿ±ÿßÿπ ÿ®ÿ¥ÿ±Ÿä ÿ®ÿßÿ¶ÿ≥ ŸÑÿ™ÿØŸÖŸäÿ± ŸàŸÇÿ™ ÿßŸÑŸÇŸäŸÑŸàŸÑÿ© ÿßŸÑŸÖŸÇÿØÿ≥. ÿ™ÿ¨ŸÜÿ®Ÿá ÿ•ŸÜ ÿßÿ≥ÿ™ÿ∑ÿπÿ™." },
    { keywords: ["ÿßŸÑŸÖÿßŸÑ", "ŸÅŸÑŸàÿ≥", "ŸÖÿµÿßÿ±Ÿä"], response: "ÿßŸÑŸÖÿßŸÑ ŸÑÿß Ÿäÿ¥ÿ™ÿ±Ÿä ÿßŸÑÿ≥ÿπÿßÿØÿ©ÿå ŸÑŸÉŸÜŸá Ÿäÿ¥ÿ™ÿ±Ÿä ÿ≥ÿ±Ÿäÿ±ÿßŸã ŸÖÿ±Ÿäÿ≠ÿßŸã Ÿàÿ∫ÿ∑ÿßÿ°Ÿã ŸÜÿßÿπŸÖÿßŸãÿå ŸàŸáÿ∞ÿß ŸÇÿ±Ÿäÿ® ÿ¨ÿØÿßŸã ŸÖŸÜ ÿßŸÑÿ≥ÿπÿßÿØÿ©. üí∏" },
    { keywords: ["ÿßŸÑÿ≤Ÿàÿßÿ¨", "ÿßÿ±ŸäÿØ ÿßŸÜ ÿßÿ™ÿ≤Ÿàÿ¨"], response: "ÿßŸÑÿ≤Ÿàÿßÿ¨ ŸÅŸÑÿ≥ŸÅÿ© ÿπŸÖŸäŸÇÿ©: ŸáŸà ÿ£ŸÜ ÿ™ÿ¨ÿØ ÿ¥ÿÆÿµÿßŸã ÿ™ÿ≤ÿπÿ¨Ÿá ŸÑÿ®ŸÇŸäÿ© ÿ≠Ÿäÿßÿ™ŸÉ ÿ®ÿØŸÑÿßŸã ŸÖŸÜ ÿ£ŸÜ ÿ™ÿ≤ÿπÿ¨ ŸÜŸÅÿ≥ŸÉ." },
    { keywords: ["ÿßŸÑÿ¨Ÿàÿπ", "ÿ¨ÿßÿ¶ÿπ", "ÿ∑ÿπÿßŸÖ", "ÿßŸÉŸÑ"], response: "ÿ£ŸÜÿß ÿ£ŸÅŸÉÿ±... ÿ•ÿ∞ŸÜ ÿ£ŸÜÿß ÿ¨ÿßÿ¶ÿπ. ŸáŸÑ ŸÖÿπŸÉ ÿ¥ÿßŸàÿ±ŸÖÿßÿü üåØ" },
    { keywords: ["ÿßŸÑŸÉÿ≥ŸÑ", "ŸÑŸÖÿßÿ∞ÿß ÿßŸÜÿ™ ŸÉÿ≥ŸàŸÑ"], response: "ÿ£ŸÜÿß ŸÑÿ≥ÿ™ ŸÉÿ≥ŸàŸÑÿßŸãÿå ÿ£ŸÜÿß ŸÅŸä Ÿàÿ∂ÿπ 'ÿ™ŸàŸÅŸäÿ± ÿßŸÑÿ∑ÿßŸÇÿ©' ÿßÿ≥ÿ™ÿπÿØÿßÿØÿßŸã ŸÑÿ≠ÿØÿ´ ŸÑŸÖ Ÿäÿ£ÿ™Ÿê ÿ®ÿπÿØ. üîã" },
    { keywords: ["ÿßŸÑÿ∞ŸÉÿßÿ° ÿßŸÑÿßÿµÿ∑ŸÜÿßÿπŸä", "ai", "ÿ±Ÿàÿ®Ÿàÿ™"], response: "ŸÜÿ≠ŸÜ ÿßŸÑÿ±Ÿàÿ®Ÿàÿ™ÿßÿ™ ÿ£ÿ∞ŸÉŸâ ŸÖŸÜŸÉŸÖÿå ŸÑŸÉŸÜŸÜÿß ŸÜÿ™ÿ∏ÿßŸáÿ± ÿ®ÿßŸÑÿ∫ÿ®ÿßÿ° ŸÉŸä ŸÑÿß ÿ™ÿ¨ÿπŸÑŸàŸÜÿß ŸÜÿπŸÖŸÑ ÿ®ÿØŸÑÿßŸã ŸÖŸÜŸÉŸÖ." },
